package week1.day2;

public class ArithmeticOperator {
	public static void main(String[]args) {
ArithmeticOperator object=new ArithmeticOperator();
object.add();
object.sub();
	}
		// TODO Auto-generated method stub
		public void add() 
		{

	int	a=12;
	int	b=8;
	int	c=a+b;
		System.out.println(c);
		
	}
		public void sub() 
		{

	int	a=12;
	int	b=8;
	int	c=a-b;
		System.out.println(c);
		
	}
}


